/**
 * IDNA/Punycode implementation
 *
 * This is a standalone implementation, and does not need any external
 * libraries (except for mbstring).
 *
 * @package Requests
 * @subpackage IDNA
 */
class Requests_IDNA {
	/**
	 * ACE prefix used for IDNA
	 *
	 * @var string
	 */
	const ACE_PREFIX = 'xn--';

	/**
	 * Get a synonym for a UCS-4 value
	 *
	 * @param int $char
	 * @return int|null
	 */
	public static function get_synonym($char) {
		// First, check for characters that can be canonicalized
		if (isset(self::$synonym_map[$char])) {
			return self::$synonym_map[$char];
		}

		// Then, check for ranges
		foreach (self::$synonym_map_range as $range) {
			if ($char >= $range[0] && $char <= $range[1]) {
				// We have a synonym!
				return $range[2] + ($char - $range[0]);
			}
		}

		return null;
	}

	/**
	 * Encode a domain name
	 *
	 * @param string $domain
	 * @return string
	 */
	public static function encode($domain) {
		$parts = explode('.', $domain);
		foreach ($parts as &$part) {
			$part = self::to_ascii($part);
		}
		return implode('.', $parts);
	}

	/**
	 * Decode a domain name
	 *
	 * @param string $domain
	 * @return string
	 */
	public static function decode($domain) {
		$parts = explode('.', $domain);
		foreach ($parts as &$part) {
			$part = self::to_unicode($part);
		}
		return implode('.', $parts);
	}

	/**
	 * Convert a UTF-8 string to an array of UCS-4 values
	 *
	 * @param string $string
	 * @return array
	 */
	protected static function utf8_to_ucs4($string) {
		$ucs4 = array();

		// Get the bytes
		$bytes = array_map('ord', str_split($string));
		$length = count($bytes);

		for ($i = 0; $i < $length; $i++) {
			$char = $bytes[$i];

			if ($char < 0x80) {
				// 0-127, single byte
				$ucs4[] = $char;
				continue;
			}

			if (($char >> 5) === 0x06) {
				// 110xxxxx 10xxxxxx
				$ucs4[] = (($char & 0x1F) << 6) | ($bytes[++$i] & 0x3F);
				continue;
			}

			if (($char >> 4) === 0x0E) {
				// 1110xxxx 10xxxxxx 10xxxxxx
				$ucs4[] = (($char & 0x0F) << 12) | (($bytes[++$i] & 0x3F) << 6) | ($bytes[++$i] & 0x3F);
				continue;
			}

			if (($char >> 3) === 0x1E) {
				// 11110xxx 10xxxxxx 10xxxxxx 10xxxxxx
				$ucs4[] = (($char & 0x07) << 18) | (($bytes[++$i] & 0x3F) << 12) | (($bytes[++$i] & 0x3F) << 6) | ($bytes[++$i] & 0x3F);
				continue;
			}
		}

		return $ucs4;
	}

	/**
	 * Convert an array of UCS-4 values to a UTF-8 string
	 *
	 * @param array $ucs4
	 * @return string
	 */
	protected static function ucs4_to_utf8($ucs4) {
		$string = '';

		foreach ($ucs4 as $char) {
			if ($char < 0x80) {
				$string .= chr($char);
			}
			elseif ($char < 0x800) {
				$string .= chr(0xC0 | $char >> 6);
				$string .= chr(0x80 | $char & 0x3F);
			}
			elseif ($char < 0x10000) {
				$string .= chr(0xE0 | $char >> 12);
				$string .= chr(0x80 | $char >> 6 & 0x3F);
				$string .= chr(0x80 | $char & 0x3F);
			}
			else {
				$string .= chr(0xF0 | $char >> 18);
				$string .= chr(0x80 | $char >> 12 & 0x3F);
				$string .= chr(0x80 | $char >> 6 & 0x3F);
				$string .= chr(0x80 | $char & 0x3F);
			}
		}

		return $string;
	}

	/**
	 * Convert a UTF-8 string to ASCII
	 *
	 * @param string $string
	 * @return string
	 */
	protected static function to_ascii($string) {
		// Step 1: Canonicalize
		$string = self::utf8_to_ucs4(Normalizer::normalize($string, Normalizer::FORM_C));

		// Step 2: Drop all synonyms
		$string_new = array();
		foreach ($string as $char) {
			$synonym = self::get_synonym($char);
			if ($synonym !== null) {
				$string_new[] = $synonym;
			}
			else {
				$string_new[] = $char;
			}
		}
		$string = $string_new;

		// Step 3: Check for non-ASCII characters
		$has_non_ascii = false;
		foreach ($string as $char) {
			if ($char > 0x7F) {
				$has_non_ascii = true;
				break;
			}
		}

		if (!$has_non_ascii) {
			// All ASCII, so we can just return
			return self::ucs4_to_utf8($string);
		}

		// Step 4: Punycode
		return self::ACE_PREFIX . self::punycode_encode($string);
	}

	/**
	 * Convert a string to Unicode.
	 *
	 * @param string $string
	 * @return string
	 */
	protected static function to_unicode($string) {
		// Check for the ACE prefix
		if (strpos($string, self::ACE_PREFIX) !== 0) {
			return $string;
		}

		$string = substr($string, strlen(self::ACE_PREFIX));
		$decoded = self::punycode_decode($string);
		return self::ucs4_to_utf8($decoded);
	}

	/**
	 * Punycode-encode a UCS-4 string
	 *
	 * @param array $string
	 * @return string
	 */
	protected static function punycode_encode($input) {
		$output = '';
		$n = 0x80;
		$delta = 0;
		$bias = 72;

		$b = 0;
		foreach ($input as $char) {
			if ($char < 0x80) {
				$b++;
				$output .= chr($char);
			}
		}
		$h = $b;
		if ($b > 0) {
			$output .= '-';
		}

		while ($h < count($input)) {
			$m = 0x7FFFFFFF;
			foreach ($input as $char) {
				if ($char >= $n && $char < $m) {
					$m = $char;
				}
			}

			if (($m - $n) > floor((0x7FFFFFFF - $delta) / ($h + 1))) {
				// Overflow
				throw new Requests_Exception('Integer overflow', 'idna.overflow');
			}

			$delta = $delta + ($m - $n) * ($h + 1);
			$n = $m;

			foreach ($input as $char) {
				if ($char < $n) {
					$delta++;
					if (0 === $delta) {
						// Overflow
						throw new Requests_Exception('Integer overflow', 'idna.overflow');
					}
				}

				if ($char === $n) {
					$q = $delta;
					for ($k = 36; ; $k += 36) {
						$t = 0;
						if ($k <= $bias) {
							$t = 1;
						}
						elseif ($k >= $bias + 26) {
							$t = 26;
						}
						else {
							$t = $k - $bias;
						}

						if ($q < $t) {
							break;
						}

						$digit = $t + (($q - $t) % (36 - $t));
						$output .= self::digit_to_char($digit);

						$q = floor(($q - $t) / (36 - $t));
					}

					$output .= self::digit_to_char($q);
					$bias = self::adapt($delta, $h + 1, ($h === $b));
					$delta = 0;
					$h++;
				}
			}

			$delta++;
			$n++;
		}
		return $output;
	}

	protected static function punycode_decode($input) {
		$output = array();
		$n = 0x80;
		$i = 0;
		$bias = 72;

		$last_dash = strrpos($input, '-');
		if ($last_dash !== false) {
			$basic = substr($input, 0, $last_dash);
			foreach (str_split($basic) as $char) {
				if (ord($char) > 0x7F) {
					// Character is non-ASCII
					throw new Requests_Exception('Invalid punycode string', 'idna.invalid_string');
				}
				$output[] = ord($char);
			}
			$input = substr($input, $last_dash + 1);
		}

		while (strlen($input) > 0) {
			$oldi = $i;
			$w = 1;
			for ($k = 36; ; $k += 36) {
				$digit = self::char_to_digit(substr($input, 0, 1));
				$input = substr($input, 1);
				$i = $i + $digit * $w;
				if ($i > 0x7FFFFFFF) {
					// Integer overflow
					throw new Requests_Exception('Integer overflow', 'idna.overflow');
				}

				$t = 0;
				if ($k <= $bias) {
					$t = 1;
				}
				elseif ($k >= $bias + 26) {
					$t = 26;
				}
				else {
					$t = $k - $bias;
				}

				if ($digit < $t) {
					break;
				}

				$w = $w * (36 - $t);
			}
			$bias = self::adapt($i - $oldi, count($output) + 1, ($oldi === 0));
			$n = $n + floor($i / (count($output) + 1));
			$i = $i % (count($output) + 1);

			if ($n > 0x7FFFFFFF) {
				// Integer overflow
				throw new Requests_Exception('Integer overflow', 'idna.overflow');
			}
			if (strlen($input) > 0 && $i > strlen($input)) {
				throw new Requests_Exception('Invalid punycode string', 'idna.invalid_string');
			}

			array_splice($output, $i, 0, array($n));
			$i++;
		}

		return $output;
	}

	protected static function adapt($delta, $numpoints, $firsttime) {
		if ($firsttime) {
			$delta = floor($delta / 700); // 700 = damp
		}
		else {
			$delta = floor($delta / 2);
		}

		$delta += floor($delta / $numpoints);

		$k = 0;
		while ($delta > 455) { // 455 = ((base - tmin) * tmax) / 2
			$delta = floor($delta / 35); // 35 = base - tmin
			$k += 36;
		}

		$k += floor((36 * $delta) / ($delta + 38)); // 38 = skey
		return $k;
	}

	protected static function char_to_digit($char) {
		$ord = ord($char);
		// a-z
		if ($ord >= 0x61 && $ord <= 0x7A) {
			return $ord - 0x61;
		}
		// A-Z
		if ($ord >= 0x41 && $ord <= 0x5A) {
			return $ord - 0x41;
		}
		// 0-9
		if ($ord >= 0x30 && $ord <= 0x39) {
			return $ord - 0x30 + 26;
		}
		throw new Requests_Exception('Invalid punycode character', 'idna.invalid_character');
	}

	protected static function digit_to_char($digit) {
		// a-z
		if ($digit < 26) {
			return chr($digit + 0x61);
		}
		// 0-9
		if ($digit < 36) {
			return chr($digit - 26 + 0x30);
		}
		throw new Requests_Exception('Invalid punycode digit', 'idna.invalid_digit');
	}

